<?php
 namespace MailPoetVendor\Twig\Error; if (!defined('ABSPATH')) exit; class SyntaxError extends \MailPoetVendor\Twig\Error\Error { public function addSuggestions($name, array $items) { if (!($alternatives = self::computeAlternatives($name, $items))) { return; } $this->appendMessage(\sprintf(' Did you mean "%s"?', \implode('", "', $alternatives))); } public static function computeAlternatives($name, $items) { $alternatives = []; foreach ($items as $item) { $lev = \levenshtein($name, $item); if ($lev <= \strlen($name) / 3 || \false !== \strpos($item, $name)) { $alternatives[$item] = $lev; } } \asort($alternatives); return \array_keys($alternatives); } } \class_alias('MailPoetVendor\\Twig\\Error\\SyntaxError', 'MailPoetVendor\\Twig_Error_Syntax'); 